// Module dependencies
var refreshTokensDAO = require('../internal/dao').refreshTokens;

var http = require('http');
var querystring = require('querystring');

var log = require('oae-logger').logger('oae-doodle-rest');

exports.save = function (userId, refreshToken, callback) {
	refreshTokensDAO.save(userId, refreshToken, callback);
};

exports.get = function (userId, callback) {
	refreshTokensDAO.find(userId, callback);
};